import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import { Formik } from "formik";
import { useDispatch } from "react-redux";
import { fetchListByDate, } from "../../../redux/actions/searchActions";
import Button from "@material-ui/core/Button";
import SearchIcon from "@material-ui/icons/Search";
import DateFnsUtils from '@date-io/date-fns';
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';

const useStyles = makeStyles((theme) => ({
    root: {
        '& > *': {
            margin: theme.spacing(1),
            width: '25ch',
        },
    },
}));

export default function Search() {
    const classes = useStyles();
    const dispatch = useDispatch();
    const [selectedDate, setSelectedDate] = React.useState(new Date('2014-08-18T21:11:54'));
    const handleDateChange = (date) => {
      setSelectedDate(date);
    };
    return (
        <div style={{ display: "flexbox", justifyContent: "center", textAlign: "center", padding: "30px" }}>
            <Formik
                initialValues={{ createdAt: "" }}
                onSubmit={(values) => {
                    dispatch(fetchListByDate(values));
                    console.log(values, "values");

                    // setTimeout(() => {
                    //     alert(JSON.stringify(values, null, 2));
                    //     setSubmitting(false);
                    // }, 400);
                }}
            >
                {({
                    values,
                    errors,
                    touched,
                    handleChange,
                    handleBlur,
                    handleSubmit,
                    isSubmitting,
                    /* and other goodies */
                }) => (
                        <form className={classes.root} noValidate autoComplete="off" onSubmit={handleSubmit}>
                            {/* <TextField
                                id="standard-secondary"
                                label="Cari Berdasar Tanggal"
                                color="primary"
                                onBlur={handleBlur}
                                onChange={handleChange}
                                value={values.createdAt}
                            /> */}
                            <KeyboardDatePicker
                                margin="normal"
                                id="date-picker-dialog"
                                label="Date picker dialog"
                                format="MM/dd/yyyy"
                                value={selectedDate}
                                onChange={handleDateChange}
                                KeyboardButtonProps={{
                                    'aria-label': 'change date',
                                }}
                            />

                            <Button
                                // variant="contained"
                                color="primary"
                                type="submit"
                                disabled={isSubmitting}
                                style={{ marginLeft: 16, width: "1ch", marginTop: "20px" }}
                            >
                                <SearchIcon />
                            </Button>
                            <p style={{ color: "red", fontStyle: "italic" }}>
                                {errors.createdAt &&
                                    touched.createdAt &&
                                    errors.createdAt}
                            </p>
                        </form>
                    )}
            </Formik>
        </div>
    );
}
