import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Header from '../header/Header'
import { TableBody, TableCell, Table, } from '@material-ui/core';
import TableRow from "@material-ui/core/TableRow";
import Typography from "@material-ui/core/Typography";
import Button from '@material-ui/core/Button';
import { useDispatch, useSelector } from "react-redux";
import { getUser } from '../../../redux/actions/userActions';
import { useParams } from "react-router-dom"


const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        display: "flex",
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));

export default function Profile() {
    const classes = useStyles();
    const profile = useSelector((state) => {
        // console.log(state, "ini state");

        return state.userList;
    });

    const dispatch = useDispatch();

    const getProfile = localStorage.getItem("userid");

    const { id } = useParams();

    useEffect(() => {
        dispatch(getUser(getProfile))
    }, [dispatch, getProfile]);
    return (
        <div>
            <Header />
            <div className={classes.root} >
                <Grid container spacing={3} style={{ display: "flex", justifyContent: "center" }} >
                    <Grid item xs={6} >
                        <Paper className={classes.paper} style={{ marginTop: "30px" }}>
                            <Table>
                                <TableBody>
                                    <TableRow>
                                        {/* <TableCell> */}
                                        <h1>Profil Anda</h1>
                                        {/* </TableCell> */}
                                    </TableRow>
                                    <TableRow>
                                        <TableCell>
                                            {/* <li style={{textDecoration:"none"}}> */}
                                            <Typography className={classes.font}>
                                                Nama Depan : {profile.data !== undefined ? profile.data[0].first_name: null}
                                                </Typography>
                                            {/* </li> */}
                                        </TableCell>
                                    </TableRow>
                                    <TableRow>
                                        <TableCell>
                                            {/* <li style={{textDecoration:"none"}}> */}
                                            <Typography className={classes.font}>
                                                Nama Belakang : {profile.data !== undefined ? profile.data[0].last_name: null}
                                                </Typography>
                                            {/* </li> */}
                                        </TableCell>
                                    </TableRow>
                                    <TableRow>
                                        <TableCell>
                                            {/* <li style={{textDecoration:"none"}}> */}
                                            <Typography className={classes.font}>
                                                Email : {profile.data !== undefined ? profile.data[0].email: null}
                                                </Typography>
                                            {/* </li> */}
                                        </TableCell>
                                    </TableRow>
                                    <TableRow>
                                        <TableCell>
                                            {/* <li style={{textDecoration:"none"}}> */}
                                            <Typography className={classes.font}>
                                                Password : {profile.data !== undefined ? profile.data[0].password: null}
                                                </Typography>
                                            {/* </li> */}
                                        </TableCell>
                                    </TableRow>
                                </TableBody>
                            </Table>
                            <Button variant="outlined" color="primary" style={{margin:"20px"}}>
                                Rubah
                            </Button>
                            <Button variant="outlined" color="primary" style={{margin:"20px"}}>
                                Kembali
                            </Button>
                        </Paper>
                    </Grid>

                </Grid>
            </div>
        </div>
    );
}
