import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Button from '@material-ui/core/Button';
import { Formik } from 'formik';
import TextField from '@material-ui/core/TextField';
import { useDispatch, useSelector } from "react-redux";

const profile = useSelector((state) => {
  // console.log(state, "ini state");

  return state.userList.user;
});

function rand() {
  return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

export default function SimpleModal() {
  const classes = useStyles();
  // getModalStyle is not a pure function, we roll the style only on the first render
  const [modalStyle] = React.useState(getModalStyle);
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const body = (
    <div style={modalStyle} className={classes.paperModal}>
      <h2 id="simple-modal-title">Ubah Profil</h2>
      <Formik
        initialValues={{
          email: profile.email,
          password: profile.password,
          first_name: profile.first_name,
          last_name: profile.last_name,
        }}
        validate={values => {
          const errors = {};
          // if (!values.first_name) {
          //     errors.first_name = "Required";
          // }
          // if (!values.last_name) {
          //     errors.last_name = "Required";
          // }
          // if (!values.password) {
          //     errors.password = 'Required';
          // }
          // if (!values.email) {
          //     errors.email = 'Required';
          // } 
          if (
            !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
          ) {
            errors.email = 'Invalid email address';
          }
          return errors;
        }}
        onSubmit={(values, { setSubmitting }) => {
          // setTimeout(() => {
          // console.log(values)

          // alert(JSON.stringify(values));
          //     setSubmitting(false);
          // }, 400);
          handleSave(values)
        }}
      >
        {({
          values,
          errors,
          touched,
          handleChange,
          handleBlur,
          handleSubmit,
          isSubmitting,
          /* and other goodies */
        }) => (
            <form
              className={classes.form}
              noValidate
              onSubmit={handleSubmit}>
              <TextField
                className={classes.textField}
                required id="standard-required"
                label="Nama Depan"
                defaultValue=""
                autoComplete="first_name"
                name="first_name"
                onChange={handleChange}
                value={values.first_name}
                onBlur={handleBlur}
              />
              <p
                style={{
                  color: "red",
                  fontStyle: "italic",
                }}
              >
                {errors.first_name &&
                  touched.first_name &&
                  errors.first_name}
              </p>
              <TextField
                className={classes.textField}
                required id="standard-required"
                label="Nama Belakang"
                defaultValue=""
                name="last_name"
                autoComplete="last_name"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.last_name}
              />
              <p
                style={{
                  color: "red",
                  fontStyle: "italic",
                }}
              >
                {errors.last_name &&
                  touched.last_name &&
                  errors.last_name}
              </p>
              <TextField
                className={classes.textField}
                required id="standard-required"
                label="Email"
                defaultValue=""
                name="email"
                autoComplete="email"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.email}
              />
              <p
                style={{
                  color: "red",
                  fontStyle: "italic",
                }}
              >
                {errors.email &&
                  touched.email &&
                  errors.email}
              </p>
              <TextField
                className={classes.textField}
                required id="standard-required"
                label="Password"
                defaultValue=""
                name="password"
                type="password"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.password}
              />
              <p
                style={{
                  color: "red",
                  fontStyle: "italic",
                }}
              >
                {errors.password &&
                  touched.password &&
                  errors.password}
              </p>
              {/* <br /> */}
              <div style={{ margin: "20px", display: "flex", justifyContent: "center", }}>
                <Button type="submit" variant="outlined" color="primary" className={classes.button} disabled={isSubmitting}>
                  Simpan
                            </Button>
                <Button variant="outlined" color="primary" className={classes.button} onClick={handleClose}>
                  Batal
                            </Button>
              </div>
            </form>
          )}
      </Formik>
    </div>
  );

  return (
    <div>
      {/* <button type="button" onClick={handleOpen}>
        Detail
      </button> */}
      {/* <Button size="small" onClick={handleOpen}>Detail</Button> */}

      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal>
    </div>
  );
}
