import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Header from '../header/Header'
import { TableBody, TableCell, Table, } from '@material-ui/core';
import TableRow from "@material-ui/core/TableRow";
import Typography from "@material-ui/core/Typography";
import Button from '@material-ui/core/Button';
import { useDispatch, useSelector } from "react-redux";
import { getUser } from '../../../redux/actions/userActions';
import { Link} from "react-router-dom"
import Modal from '@material-ui/core/Modal';
import TextField from '@material-ui/core/TextField';
import { Formik } from 'formik';


function rand() {
    return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        display: "flex",
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
    paperModal: {
        position: 'absolute',
        width: 400,
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
    textField: {
        margin: "10px"
    },
    button: {
        margin: "10px"
    }
}));

export default function Profile() {
    const classes = useStyles();
    const profile = useSelector((state) => {
        // console.log(state, "ini state");

        return state.userList;
    });

    const dispatch = useDispatch();

    const getProfile = localStorage.getItem("userid");
    const [modalStyle] = React.useState(getModalStyle);
    const [open, setOpen] = React.useState(false);
    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const body = (
        <div style={modalStyle} className={classes.paperModal}>
            <h2 id="simple-modal-title">Ubah Profil</h2>
            <Formik
                initialValues={{ email: '', password: '' }}
                validate={values => {
                    const errors = {};
                    if (!values.email) {
                        errors.email = 'Required';
                    } else if (
                        !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                    ) {
                        errors.email = 'Invalid email address';
                    }
                    return errors;
                }}
                onSubmit={(values, { setSubmitting }) => {
                    setTimeout(() => {
                        alert(JSON.stringify(values, null, 2));
                        setSubmitting(false);
                    }, 400);
                }}
            >
                {({
                    values,
                    errors,
                    touched,
                    handleChange,
                    handleBlur,
                    handleSubmit,
                    isSubmitting,
                    /* and other goodies */
                }) => (
                        <form onSubmit={handleSubmit}>
                            <TextField
                                className={classes.textField}
                                required id="standard-required"
                                label="Nama Depan"
                                defaultValue="" />
                            <TextField
                                className={classes.textField}
                                required id="standard-required"
                                label="Nama Belakang"
                                defaultValue="" />
                            <TextField
                                className={classes.textField}
                                required id="standard-required"
                                label="Email"
                                defaultValue="" />
                            <TextField
                                className={classes.textField}
                                required id="standard-required"
                                label="Password"
                                defaultValue="" />
                            {/* <br /> */}
                            <div style={{ margin: "20px", display: "flex", justifyContent: "center",  }}>
                                <Button variant="outlined" color="primary" >
                                    Save
                                </Button>
                                <Button variant="outlined" color="primary" >
                                    Cancel
                                </Button>
                            </div>
                        </form>
                    )}
            </Formik>
        </div>
    );

    useEffect(() => {
        dispatch(getUser(getProfile))
    }, [dispatch, getProfile]);
    return (
        <div>
            <Header />
            <div className={classes.root} >
                <Grid container spacing={3} style={{ display: "flex", justifyContent: "center" }} >
                    <Grid item xs={6} >
                        <Paper className={classes.paper} style={{ marginTop: "30px" }}>
                            <Table>
                                <TableBody>
                                    <TableRow>
                                        {/* <TableCell> */}
                                        <h1>Profil Anda</h1>
                                        {/* </TableCell> */}
                                    </TableRow>
                                    <TableRow>
                                        <TableCell>
                                            {/* <li style={{textDecoration:"none"}}> */}
                                            <Typography className={classes.font}>
                                                Nama Depan : {profile.data !== undefined ? profile.data[0].first_name : null}
                                            </Typography>
                                            {/* </li> */}
                                        </TableCell>
                                    </TableRow>
                                    <TableRow>
                                        <TableCell>
                                            {/* <li style={{textDecoration:"none"}}> */}
                                            <Typography className={classes.font}>
                                                Nama Belakang : {profile.data !== undefined ? profile.data[0].last_name : null}
                                            </Typography>
                                            {/* </li> */}
                                        </TableCell>
                                    </TableRow>
                                    <TableRow>
                                        <TableCell>
                                            {/* <li style={{textDecoration:"none"}}> */}
                                            <Typography className={classes.font}>
                                                Email : {profile.data !== undefined ? profile.data[0].email : null}
                                            </Typography>
                                            {/* </li> */}
                                        </TableCell>
                                    </TableRow>
                                    <TableRow>
                                        <TableCell>
                                            {/* <li style={{textDecoration:"none"}}> */}
                                            <Typography className={classes.font}>
                                                Password : {profile.data !== undefined ? profile.data[0].password : null}
                                            </Typography>
                                            {/* </li> */}
                                        </TableCell>
                                    </TableRow>
                                </TableBody>
                            </Table>
                            <Button variant="outlined" color="primary" onClick={handleOpen} style={{ margin: "20px" }}>
                                Rubah
                            </Button>
                            <Modal
                                open={open}
                                onClose={handleClose}
                                aria-labelledby="simple-modal-title"
                                aria-describedby="simple-modal-description"
                            >
                                {body}
                            </Modal>
                            <Button variant="outlined" color="primary" style={{ margin: "20px" }}>
                                <Link
                                    variant="button"
                                    color="textPrimary"
                                    to="/main-page"
                                    className={classes.link}
                                    style={{ textDecoration: "none" }}
                                >
                                    Kembali ke Menu Utama
                                </Link>
                            </Button>
                        </Paper>
                    </Grid>

                </Grid>
            </div>
        </div>
    );
}
